import React from "react";
import { inputType, inputDefaults } from "../types";
import Error from "./Error";
import Label from "./Label";

const Input = ({ name, value, label, error, touched, ...rest }) => {
  return (
    <div>
      <Label text={label} />
      <input type="text" name={name} value={value} {...rest} />
      <Error touched={touched} error={error} />
    </div>
  );
};

Input.propTypes = inputType;
Input.defaultProps = inputDefaults;

export default Input;
